(317, 1, '2015-09-10 14:28:57', '2015-09-10 14:28:57', '<div id="mid1" class="layer" data-depth="0">\r\n<p id="fore1">RĪGAS STRADIŅA UNIVERSITĀTES</p>\r\n<div id="foreLine"></div>\r\n<p id="fore2">VĒSTURES VIRTUĀLĀ EKSPOZĪCIJA</p></div>\r\n<div id="mid2" class="layer" data-depth="0">\r\n<p id="fore3" class="layer" data-depth="0">\r\nRSU vēstures virtuālā ekspozīcija, izmantojot RSU muzejā sakopotos materiālus,\r\npalīdzēs Jums iepazīties ar to, kā noritējusi mūsu augstskolas attīstība dažādos\r\nvēsturiskajos laikposmos.\r\n</p>\r\n</div>', 'Titullapa', '', 'publish', 'closed', 'closed', '', 'titullapa', '', '', '2015-09-18 08:10:34', '2015-09-18 08:10:34', '', 0, 'http://rsu.nanolv.lv/?page_id=317', 1, 'page', '', 0),
(318, 1, '2015-09-10 14:28:57', '2015-09-10 14:28:57', '<div id="mid1" class="layer" data-depth="0.15">\r\n<p id="fore1">RĪGAS STRADIŅA UNIVERSITĀTES</p>\r\n<p id="fore2">VĒSTURES VIRTUĀLĀ EKSPOZĪCIJA</p></div>\r\n<div id="mid2" class="layer" data-depth="0.15">\r\n<p id="fore3" class="layer" data-depth="0.05">\r\nRSU vēstures virtuālā ekspozīcija, izmantojot RSU muzejā sakopotos materiālus,\r\npalīdzēs Jums iepazīties ar to, kā noritējusi mūsu augstskolas attīstība dažādos\r\nvēsturiskajos laikposmos.\r\n</p>\r\n</div>', 'Titullapa', '', 'inherit', 'open', 'open', '', '317-revision-v1', '', '', '2015-09-10 14:28:57', '2015-09-10 14:28:57', '', 317, 'http://rsu.nanolv.lv/index.php/2015/09/10/317-revision-v1/', 0, 'revision', '', 0),
(319, 1, '2015-09-10 14:29:49', '2015-09-10 14:29:49', '<p class="main_text_single citation">Rīgas Stradiņa universitātes nosaukuma vēsture ir samērā nesena – tā savu tagadējo vārdu ieguva tikai 1998. gadā. Tomēr pašas augstskolas vēsture ir pietiekami gara, lai RSU varētu uzskatīt par vienu no tradīcijām bagātākajām augstākajām mācību iestādēm Latvijā. Šodien RSU ir moderna, konkurētspējīga augstskola, kurā dominē divi plašāki studiju virzieni – veselības aprūpe un sociālās zinātnes.</p>', 'Ievads', '', 'publish', 'closed', 'closed', '', 'ievads-2', '', '', '2015-11-09 16:40:33', '2015-11-09 16:40:33', '', 317, 'http://rsu.nanolv.lv/?page_id=319', 1, 'page', '', 0);
